import React, { useState, useEffect } from 'react';
import { Oval } from 'react-loader-spinner';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFrown } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import { Button, useToast } from '@chakra-ui/react';
import './App.css';
import Footer from './components/Footer';
import Header from './components/Header';

const App = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const toast = useToast();

  // useEffect to fetch weather data when 'city' state changes
  useEffect(() => {
    const handleSubmit = async () => {
      setLoading(true);
      setError(false);

      try {
        const response = await axios.post("http://localhost:3001/weather", { city });
        setWeather(response.data);
      } catch (err) {
        console.log(err);
        setError(true);
        toast({
          title: "Error fetching weather data.",
          description: "Please check the city name and try again.",
          status: "error",
          duration: 1000,
          isClosable: true,
        });
      } finally {
        setLoading(false);
      }
    };

    if (city) { // Ensure city is not empty before fetching data
      handleSubmit();
    }
  }, [city, toast]); // Dependency array ensures useEffect runs on city or toast change

  // Function to handle input changes in the city search input field
  const handleOnChange = (e) => {
    setCity(e.target.value);
  }

  // Function to convert temperature from Kelvin to Celsius
  const kelvinToCelsius = (kelvin) => {
    return (kelvin - 273.15).toFixed(2);
  }

  return (
    <div className='App'>
      <Header weather={weather} />
      <h1 className='app-name'>Weather App</h1>
      <div className='search-bar'>
        <input
          className='city-search'
          type='text'
          placeholder='Enter city name'
          onChange={handleOnChange} 
          value={city}
        />
        <Button className="get-weather-btn" onClick={handleSubmit} size={"sm"}>Get Weather</Button>
      </div>

      {loading && (
        <div className='loading'>
          <Oval
            height={40}
            width={40}
            color='blue'
            ariaLabel='loading'
          />
        </div>
      )}

      {!loading && weather && (
        <div className="weather-info">
          <div className="city-name">
            <h2>
              Weather in {weather.name}
            </h2>

            <div className='icon-temp'>
              <img 
                className='weather-icon'
                src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
                alt={weather.weather[0].description}
              />
              {kelvinToCelsius(weather.main.temp)}
              <sup className="deg">Â°C</sup>
            </div>
          </div>
          <div className='des-wind'>
            <p>Weather: {weather.weather[0].description}</p>
            <p>Wind Speed: {weather.wind.speed} m/s</p>
          </div>
        </div>
      )}

      {!loading && error && (
        <div className='error'>
          <FontAwesomeIcon icon={faFrown} size='2x' />
          <p>No data available</p>
        </div>
      )}
      <Footer />
    </div>
  );
}

export default App;
