import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  // useState hooks to manage city name and weather data
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);

  // Function to handle form submission
  async function handleSubmit(e) {
    console.log("Submitted");

    try {
      // Clear previous weather data
      setWeather(null);

      // Sending a POST request to the backend with the city name
      const response = await axios.post("http://localhost:3001/weather", { city });
      setWeather(response.data); // Update weather state with response data
      console.log(response.data); // Log the response data
    } catch (err) {
      console.log(err); // Log any error if occurs
      // Display error toast
      toast({
        title: "Error fetching weather data.",
        description: "Please check the city name and try again.",
        status: "error",
        duration: 1000,
        isClosable: true,
      });
    }
  }

  // Function to handle input changes
  function handleOnChange(e) {
    setCity(e.target.value); // Update city state with input value
    console.log(e.target.value); // Log the current input value
  }

  // Function to convert Kelvin to Celsius
  const kelvinToCelsius = (kelvin) => {
    return (kelvin - 273.15).toFixed(2); // Convert and round to 2 decimal places
  }

  return (
      <div className='App'>
        <h1 className='app-name'>Weather App</h1>
        <div className='search-bar'>
          <input
            className='city-search'
            type='text'
            placeholder='Enter city name'
            onChange={handleOnChange} 
            value={city}
          />
          <Button color={"whiteAlpha800"} onClick={handleSubmit} size={"sm"}>Get Weather</Button>
        </div>

        {weather && (
        <div>

        </div>
      )}
      </div>
  );
}

export default App;
