import React from 'react';
import './Header.css';

const Header = ({ weather }) => {
  return (
    <header className="header">
      <div className="header-content">
        <div className="branding">
          <h1 className="app-name">Weatherly</h1>
          <p className="slogan">Experience the Weather Like Never Before</p>
        </div>
        <div className="weather-info">
          {weather && (
            <>
              <div className="temperature-info">
                <span className="temperature">
                  {(weather.main.temp - 273.15).toFixed(1)}Â°C
                </span>
                <span className="weather-description">{weather.weather[0].description}</span>
              </div>
              <div className="location">
                <span className="city-name">{weather.name}</span>
              </div>
            </>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
