import axios from 'axios';
import React, { useState } from 'react';

function WeatherForm() {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);

  function handleOnChange(e) {
    setCity(e.target.value);
  }

  async function handleSubmit(e) {
    e.preventDefault(); // Prevent the default form submission behavior
    try {
      const response = await axios.post('http://localhost:3001/weather', { city });
      console.log('Weather data:', response.data);
      setWeatherData(response.data); // Update state to display the weather info
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" value={city} onChange={handleOnChange} placeholder="Enter city name" />
        <button type="submit">Get Weather</button>
      </form>
      {weatherData && (
        <div>
          <h3>Weather in {weatherData.city}</h3>
          <p>Temperature: {weatherData.temperature}Â°C</p>
          <p>Description: {weatherData.description}</p>
        </div>
      )}
    </div>
  );
}

export default WeatherForm;
