import { useState } from 'react';
import axios from 'axios';

function App() {
  const [city, setCity] = useState('');

  function handleOnChange(e) {
    setCity(e.target.value);
  }

  async function handleSubmit(e) {
    e.preventDefault(); // Prevent the default form submission behavior
    try {
      const response = await axios.post('http://localhost:3001/weather', { city });
      console.log('Weather data:', response.data);
      // You can handle the response data here, such as updating state to display the weather info
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type='text' onChange={handleOnChange} placeholder='Enter city name' value={city} />
        <button type='submit'>Submit</button>
      </form>
    </div>
  );
}

export default App;
