import React, { useState } from 'react';
import axios from 'axios';
import { Button, Flex, Input, VStack } from '@chakra-ui/react'
const App = () => {
  // useState hook to manage city name and weather data
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);

  // Function to handle form submission
  async function handleSubmit(e) {
    // e.preventDefault(); // Prevent page reload
    console.log("Submitted");

    try {
      // Sending a POST request to the backend with the city name
      const response = await axios.post("http://localhost:3001/weather", { city });
      setWeather(response.data); // Update weather state with response data
      console.log(response.data); // Log the response data
    } catch (err) {
      console.log(err); // Log any error if occurs
    }
  }

  // Function to handle input changes
  function handleOnChange(e) {
    setCity(e.target.value); // Update city state with input value
    console.log(e.target.value); // Log the current input value
  }

  // Function to convert Kelvin to Celsius
  const kelvinToCelsius = (kelvin) => {
    return (kelvin - 273.15).toFixed(2); // Convert and round to 2 decimal places
  }

  return (
    <div>
        <Input
          onChange={handleOnChange} 
          type='text' 
          placeholder='Write your city name'
          width={"auto"} 
          value={city}
        />
        <Button color={"black"} onClick={handleSubmit} size={"sm"}>Get Weather</Button>
      {weather && (
        <div>
          <h2>Weather in {weather.name}</h2>
          <p>Temperature: {kelvinToCelsius(weather.main.temp)}°C</p>
          <p>Feels Like: {kelvinToCelsius(weather.main.feels_like)}°C</p>
          <p>Weather: {weather.weather[0].description}</p>
          <p>Wind Speed: {weather.wind.speed} m/s</p>
          <p>Visibility: {weather.visibility} meters</p>
        </div>
      )}
    </div>
  );
}

export default App;
