// Header.js

import React from 'react';
import { Flex, Heading, IconButton, useColorMode, useColorModeValue } from '@chakra-ui/react';
import { SunIcon, MoonIcon } from '@chakra-ui/icons';

const Header = ({ weather }) => {
  const { colorMode, toggleColorMode } = useColorMode();
  const bgColor = useColorModeValue('white', '#1a202c'); // Background color based on color mode
  const textColor = useColorModeValue('black', 'white'); // Text color based on color mode

  return (
    <Flex
      as="header"
      align="center"
      justify="space-between"
      padding="1rem"
      width="100%"
      borderBottom="1px solid #eee"
      backgroundColor={bgColor} // Set background color
      color={textColor} // Set text color
    >
      <Heading as="h1" size="lg">
        Weather App
      </Heading>

      <Flex align="center">
        {weather && (
          <>
            <img
              className='weather-icon'
              src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}.png`}
              alt={weather.weather[0].description}
              width={50}
              height={50}
              style={{ marginRight: '10px', borderRadius: '50%' }}
            />
            <Heading as="h2" size="md">
              {weather.name}, {weather.sys.country}
            </Heading>
          </>
        )}

        <IconButton
          icon={colorMode === 'light' ? <MoonIcon /> : <SunIcon />}
          onClick={toggleColorMode}
          aria-label="Toggle color mode"
          ml={3}
          backgroundColor={useColorModeValue('gray.200', 'gray.700')} // Button background color based on color mode
        />
      </Flex>
    </Flex>
  );
};

export default Header;
